// Tarot card data — all 78 cards with Polish names, meanings, and image paths

export type Arcana = "major" | "minor";
export type Suit = "cups" | "pentacles" | "swords" | "wands";

export type Element = "Ogień" | "Woda" | "Powietrze" | "Ziemia";

export interface TarotCard {
  id: string;
  number: number;
  name: string; // Polish name
  nameEn: string; // English name (for image alt text)
  arcana: Arcana;
  suit?: Suit;
  imagePath: string;
  uprightKeywords: string[];
  reversedKeywords: string[];
  uprightMeaning: string;
  reversedMeaning: string;
  imageDescription: string; // What the card depicts visually
  element: Element;
  zodiac?: string; // Associated zodiac sign(s) in Polish
  planet?: string; // Associated planet
  loveMeaning: string; // Love/relationship interpretation
  healthMeaning: string; // Health interpretation
  workMeaning: string; // Work/finance interpretation
  advice: string; // Practical takeaway
}

export interface DrawnCard {
  card: TarotCard;
  isReversed: boolean;
  timestamp: number;
}

// Suit labels in Polish
export const SUIT_NAMES: Record<Suit, string> = {
  cups: "Kielichy",
  pentacles: "Pentakle",
  swords: "Miecze",
  wands: "Różdżki",
};

export const ARCANA_NAMES: Record<Arcana, string> = {
  major: "Wielkie Arkana",
  minor: "Małe Arkana",
};

