// ─── MINOR ARCANA — helper to create cards ─────────────────────────────────

const COURT_NAMES: Record<string, { pl: string; en: string }> = {
  page: { pl: "Paź", en: "Page" },
  knight: { pl: "Rycerz", en: "Knight" },
  queen: { pl: "Królowa", en: "Queen" },
  king: { pl: "Król", en: "King" },
};

const NUMBER_NAMES: Record<number, { pl: string; en: string }> = {
  1: { pl: "As", en: "Ace" },
  2: { pl: "Dwójka", en: "Two" },
  3: { pl: "Trójka", en: "Three" },
  4: { pl: "Czwórka", en: "Four" },
  5: { pl: "Piątka", en: "Five" },
  6: { pl: "Szóstka", en: "Six" },
  7: { pl: "Siódemka", en: "Seven" },
  8: { pl: "Ósemka", en: "Eight" },
  9: { pl: "Dziewiątka", en: "Nine" },
  10: { pl: "Dziesiątka", en: "Ten" },
};

const SUIT_EN: Record<Suit, string> = {
  cups: "Cups",
  pentacles: "Pentacles",
  swords: "Swords",
  wands: "Wands",
};

const SUIT_ELEMENTS: Record<Suit, Element> = {
  cups: "Woda",
  pentacles: "Ziemia",
  swords: "Powietrze",
  wands: "Ogień",
};

interface MinorCardData {
  number: number;
  court?: string; // page, knight, queen, king
  uprightKeywords: string[];
  reversedKeywords: string[];
  uprightMeaning: string;
  reversedMeaning: string;
  imageDescription: string;
  zodiac?: string;
  planet?: string;
  loveMeaning: string;
  healthMeaning: string;
  workMeaning: string;
  advice: string;
}

function createMinorCard(suit: Suit, data: MinorCardData): TarotCard {
  const isCourt = !!data.court;
  const namePl = isCourt
    ? `${COURT_NAMES[data.court!].pl} ${SUIT_NAMES[suit]}`
    : `${NUMBER_NAMES[data.number].pl} ${SUIT_NAMES[suit]}`;
  const nameEn = isCourt
    ? `${COURT_NAMES[data.court!].en} of ${SUIT_EN[suit]}`
    : `${NUMBER_NAMES[data.number].en} of ${SUIT_EN[suit]}`;
  const fileNum = String(data.number).padStart(2, "0");
  const fileSuffix = isCourt ? data.court! : fileNum;
  const id = `${suit}-${fileSuffix}`;

  return {
    id,
    number: data.number,
    name: namePl,
    nameEn,
    arcana: "minor",
    suit,
    imagePath: `/tarot/${id}.png`,
    uprightKeywords: data.uprightKeywords,
    reversedKeywords: data.reversedKeywords,
    uprightMeaning: data.uprightMeaning,
    reversedMeaning: data.reversedMeaning,
    imageDescription: data.imageDescription,
    element: SUIT_ELEMENTS[suit],
    zodiac: data.zodiac,
    planet: data.planet,
    loveMeaning: data.loveMeaning,
    healthMeaning: data.healthMeaning,
    workMeaning: data.workMeaning,
    advice: data.advice,
  };
}

