// ─── ALL CARDS ──────────────────────────────────────────────────────────────

export const TAROT_CARDS: TarotCard[] = [
  ...MAJOR_ARCANA,
  ...CUPS,
  ...PENTACLES,
  ...SWORDS,
  ...WANDS,
];

export const MAJOR_ARCANA_CARDS = MAJOR_ARCANA;
export const MINOR_ARCANA_CARDS = [...CUPS, ...PENTACLES, ...SWORDS, ...WANDS];

/**
 * Draw a random tarot card with a random upright/reversed orientation
 */
export function drawRandomCard(): DrawnCard {
  const randomIndex = typeof crypto !== "undefined" && crypto.getRandomValues
    ? crypto.getRandomValues(new Uint32Array(1))[0] % TAROT_CARDS.length
    : Math.floor(Math.random() * TAROT_CARDS.length);

  const isReversed = typeof crypto !== "undefined" && crypto.getRandomValues
    ? (crypto.getRandomValues(new Uint8Array(1))[0] & 1) === 1
    : Math.random() > 0.5;

  return {
    card: TAROT_CARDS[randomIndex],
    isReversed,
    timestamp: Date.now(),
  };
}

/**
 * Get a card by its ID
 */
export function getCardById(id: string): TarotCard | undefined {
  return TAROT_CARDS.find((c) => c.id === id);
}
